import React from 'react';

type FormParams = Record<string, string | undefined>;

type EventProps = {
    onInit?: (submissionUuid: string) => void;
    onPageChange?: (submissionUuid: string, pageId: string) => void;
    onSubmit?: (submissionUuid: string) => void;
};

type StandardProps = {
    filloutId: string;
    domain?: string;
    inheritParameters?: boolean;
    parameters?: FormParams;
    dynamicResize?: boolean;
} & EventProps;
declare const Standard: ({ filloutId, domain, inheritParameters, parameters, dynamicResize, onInit, onPageChange, onSubmit, }: StandardProps) => React.JSX.Element;

type FullScreenProps = {
    filloutId: string;
    domain?: string;
    inheritParameters?: boolean;
    parameters?: FormParams;
} & EventProps;
declare const FullScreen: ({ filloutId, domain, inheritParameters, parameters, onInit, onPageChange, onSubmit, }: FullScreenProps) => React.JSX.Element;

type PopupProps = {
    filloutId: string;
    domain?: string;
    inheritParameters?: boolean;
    parameters?: FormParams;
    isOpen: boolean;
    onClose: () => void;
    width?: number | string;
    height?: number | string;
} & EventProps;
declare const Popup: ({ isOpen: _isOpen, onClose: _onClose, width, height, ...props }: PopupProps) => React.JSX.Element;

type ButtonSize = "small" | "medium" | "large";
type ButtonFloat = "bottomRight" | "bottomLeft";
type ButtonProps = {
    onClick?: () => void;
    text?: string;
    color?: string;
    size?: ButtonSize;
    float?: ButtonFloat;
};

type PopupButtonProps = {
    filloutId: string;
    domain?: string;
    inheritParameters?: boolean;
    parameters?: FormParams;
    width?: number | string;
    height?: number | string;
} & EventProps & Omit<ButtonProps, "onClick">;
declare const PopupButton: ({ filloutId, domain, inheritParameters, parameters, width, height, onInit, onPageChange, onSubmit, text, color, size, float, }: PopupButtonProps) => React.JSX.Element;

type SliderDirection = "left" | "right";
type SliderProps = {
    filloutId: string;
    domain?: string;
    inheritParameters?: boolean;
    parameters?: FormParams;
    sliderDirection?: SliderDirection;
    isOpen: boolean;
    onClose: () => void;
} & EventProps;
declare const Slider: ({ isOpen: _isOpen, onClose: _onClose, ...props }: SliderProps) => React.JSX.Element;

type SliderButtonProps = {
    filloutId: string;
    domain?: string;
    inheritParameters?: boolean;
    parameters?: FormParams;
    sliderDirection?: SliderDirection;
} & EventProps & Omit<ButtonProps, "onClick">;
declare const SliderButton: ({ filloutId, domain, inheritParameters, parameters, sliderDirection, onInit, onPageChange, onSubmit, text, color, size, float, }: SliderButtonProps) => React.JSX.Element;

export { FullScreen as FilloutFullScreenEmbed, Popup as FilloutPopupEmbed, PopupButton as FilloutPopupEmbedButton, Slider as FilloutSliderEmbed, SliderButton as FilloutSliderEmbedButton, Standard as FilloutStandardEmbed };
